import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom"; // Import useLocation for reading query params

const SearchUser = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [users, setUsers] = useState([]);

  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const query = params.get("query");
    if (query) {
      setSearchQuery(query);
      handleSearch(query);
    }
  }, [location]);

  const handleSearch = async (query) => {
    try {
      const response = await axios.get(
        `http://localhost:8001/api/users/search?search=${query}`
      );
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  return (
    <div className="form-table-container mt-4">
      <table className="w-full text-left border-collapse">
        <thead className="bg-gray-200">
          <tr>
            <th className="border px-4 py-2">Civilité</th>
            <th className="border px-4 py-2">Nom</th>
            <th className="border px-4 py-2">Prénom</th>
            <th className="border px-4 py-2">Adresse mail</th>
            <th className="border px-4 py-2">Maison</th>
            <th className="border px-4 py-2">Groupe de droits</th>
            <th className="border px-4 py-2">Dernière connexion</th>
          </tr>
        </thead>
        <tbody>
          <tr key={index} className="hover:bg-gray-100">
            <td className="border px-4 py-2">civilite</td>
            <td className="border px-4 py-2">nom</td>
            <td className="border px-4 py-2">prenom</td>
            <td className="border px-4 py-2">email</td>
            <td className="border px-4 py-2">maison</td>
            <td className="border px-4 py-2">groupeDroits</td>
            <td className="border px-4 py-2"></td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default SearchUser;
