import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function CreateUser() {
  const [userInput, setUserInput] = useState({
    email: "",
    password: "",
    civilite: "",
    nom: "",
    prenom: "",
    maison: "",
    groupeDroits: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInput((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate input
    if (!userInput.email || !userInput.password || !userInput.nom) {
      alert("Please fill in all required fields.");
      return;
    }

    try {
      const response = await fetch("http://localhost:3000/api/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(userInput),
      });

      const result = await response.json();
      console.log("Result:", result); // Log the result for debugging

      if (response.ok) {
        console.log("User created:", result);
        navigate("/dashboard"); // Redirect after successful creation
      } else {
        console.error("Error creating user:", result.message);
        alert(result.message || "An error occurred while creating the user.");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred while creating the user.");
    }

    // Reset form fields
    setUserInput({
      email: "",
      password: "",
      civilite: "",
      nom: "",
      prenom: "",
      maison: "",
      groupeDroits: "",
    });
  };

  return (
    <div className="create-user-container">
      <h1>User Add</h1>
      <form className="create-user-form" onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={userInput.email}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={userInput.password}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="text"
          name="civilite"
          placeholder="Civilité"
          value={userInput.civilite}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="text"
          name="nom"
          placeholder="Nom"
          value={userInput.nom}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="text"
          name="prenom"
          placeholder="Prénom"
          value={userInput.prenom}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="text"
          name="maison"
          placeholder="Maison"
          value={userInput.maison}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="text"
          name="groupeDroits"
          placeholder="Groupe de Droits"
          value={userInput.groupeDroits}
          onChange={handleChange}
          required
          className="input-field"
        />
        <button type="submit" className="submit-button">
          Add User
        </button>
      </form>
    </div>
  );
}
