import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom"; // Import useLocation for reading query params
import Sidebar from "../inc/siderbar";

const SearchUser = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [users, setUsers] = useState([]);

  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const query = params.get("query");
    if (query) {
      setSearchQuery(query);
      handleSearch(query);
    }
  }, [location]);

  const handleSearch = async (query) => {
    try {
      const response = await axios.get(`http://localhost:8001/api/users/search?search=${query}`);
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="search-container">
        {users.length > 0 && (
          <table className="user-table">
            <thead>
              <tr>
                <th>Civilité</th>
                <th>Nom</th>
                <th>Prénom</th>
                <th>Email</th>
                <th>Maison</th>
                <th>Groupe Droits</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user._id}>
                  <td>{user.civilite}</td>
                  <td>{user.nom}</td>
                  <td>{user.prenom}</td>
                  <td>{user.email}</td>
                  <td>{user.maison}</td>
                  <td>{user.groupeDroits}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        {users.length === 0 && searchQuery && (
          <p>No users found for "{searchQuery}".</p>
        )}
      </div>
    </div>
  );
};

export default SearchUser;
