import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function CreateUser() {
  const [userInput, setUserInput] = useState({
    email: "",
    nom: "",
    prenom: "",
    maison: "",
    groupeDroits: "",
  });

  const navigate = useNavigate();

  // Check if the user is logged in
  useEffect(() => {
    const token = localStorage.getItem("token"); // Get token from localStorage
    if (!token) {
      navigate("/"); // Redirect to login page if no token
    }
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInput((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("/api/users/register", { // Correct endpoint
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`, // Use token for authorization
        },
        body: JSON.stringify(userInput),
      });

      const result = await response.json();
      if (response.ok) {
        console.log("User created:", result);
        navigate("/dashboard"); // Redirect after successful creation
      } else {
        console.error("Error creating user:", result.message);
        alert(result.message || "An error occurred while creating the user."); // User feedback on error
      }
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred while creating the user."); // User feedback on error
    }

    // Reset form fields
    setUserInput({
      email: "",
      nom: "",
      prenom: "",
      maison: "",
      groupeDroits: "",
    });
  };

  return (
    <div className="create-user-container">
      <h1>User Add</h1>
      <form className="create-user-form" onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={userInput.email}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="text"
          name="nom"
          placeholder="Nom"
          value={userInput.nom}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="text"
          name="prenom"
          placeholder="Prénom"
          value={userInput.prenom}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="text"
          name="maison"
          placeholder="Maison"
          value={userInput.maison}
          onChange={handleChange}
          required
          className="input-field"
        />
        <input
          type="text"
          name="groupeDroits"
          placeholder="Groupe de Droits"
          value={userInput.groupeDroits}
          onChange={handleChange}
          required
          className="input-field"
        />
        <button type="submit" className="submit-button">
          Add User
        </button>
      </form>
    </div>
  );
}
