import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

export default function EditUser({ onClose }) {
  const { userId } = useParams();
  const [userInput, setUserInput] = useState({
    email: "",
    password: "",
    civilite: "",
    nom: "",
    prenom: "",
    maison: "",
    groupeDroits: "",
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8001/api/users/${userId}`
        );
        setUserInput(response.data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUser();
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInput({ ...userInput, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:8001/api/users/${userId}`, userInput);
      alert("User updated successfully!");
      onClose();
    } catch (error) {
      console.error("Error updating user:", error);
      alert("Error updating user.");
    }
  };

  return (
    <div className="edit-user-container">
      <Sidebar />
      <div className="content">
        <h1>Edit User</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={userInput.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password (leave blank to keep current)"
            onChange={handleChange}
          />
          <input
            type="text"
            name="civilite"
            placeholder="Civilité"
            value={userInput.civilite}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="nom"
            placeholder="Nom"
            value={userInput.nom}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="prenom"
            placeholder="Prénom"
            value={userInput.prenom}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="maison"
            placeholder="Maison"
            value={userInput.maison}
            onChange={handleChange}
            required
          />
          <select
            name="groupeDroits"
            value={userInput.groupeDroits}
            onChange={handleChange}
            required
          >
            <option value="" disabled>
              Select Groupe de Droits
            </option>
            <option value="user">User</option>
            <option value="admin">Admin</option>
          </select>
          <button type="submit">Update User</button>
        </form>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
}
